    <script>
        // Example starter JavaScript for disabling form submissions if there are invalid fields
        (function() {
            'use strict';
            window.addEventListener('load', function() {
            // Fetch all the forms we want to apply custom Bootstrap validation styles to
            var forms = document.getElementsByClassName('needs-validation');
            // Loop over them and prevent submission
            var validation = Array.prototype.filter.call(forms, function(form) {
                form.addEventListener('submit', function(event) {
                if (form.checkValidity() === false) {
                    event.preventDefault();
                    event.stopPropagation();
                }
                form.classList.add('was-validated');
                }, false);
            });
            }, false);
        })();
        // Prefent re submit on reload 
        if ( window.history.replaceState ) {
            window.history.replaceState( null, null, window.location.href );
        }


        $('#city').html('<option disabled selected>Pilih Kabupaten/Kota ...</option>');
        function getCity(){
            var province = document.getElementById('province').value;
            console.log(province);
            $.ajax({
            url:"/data/city/"+province,
            type:'get',
            dataType:'json',
            success:function (response) {
                $('#city').html('<option disabled selected>Pilih Kabupaten/Kota ...</option>');
                console.log(response.rajaongkir.results);
                response.rajaongkir.results.forEach(city => {
                    $('#city').append(new Option(city.city_name, city.city_id));
                });
            }})
        }
    </script>
